    return (
      <div className="mainDiv">
        <div className="bookDivContainer">
            <div className="rightSide">
                {firstPage.map(item => {return ` ${item.sentence}`})}
            </div>
            <div className="leftSide">
              {secondPage.map(item => {return ` ${item.sentence}`})}
            </div>
        </div>
      </div>
    );
  // }
}

export default Book;
