import React, {Component} from "react";
import "./style.css";

class Book extends Component {
  state = {
    pageOneFull: false, 
    pageTwoFull: false, 
    firstPage: [],
    secondPage: [],
    bookData: []
  }

  // static getDerivedStateFromProps(props, state) {
  //   const bookData = props.bookData
  //   console.log(bookData)
  //   setSentences(bookData)
  // }

  render() {
    const firstPage = this.props.bookData.slice(0,15)
    const secondPage = this.props.bookData.slice(15)



    return (
      <div className="mainDiv">
        <div className="bookDivContainer">
            <div className="rightSide">
                {/* {this.state.firstPage && (this.state.firstPage.map(item => {return ` ${item.sentence}`}))} */}
                {firstPage.map(item => {return ` ${item.sentence}`})}
            </div>
            <div className="leftSide">
              {secondPage.map(item => {return ` ${item.sentence}`})}
            </div>
        </div>
      </div>
    );
  }
}

export default Book;
