import React, {Component} from "react";
import "./style.css";

class Book extends Component {
  state = {

  }

  render() {
    const firstPage = this.props.bookData.slice(0,15)
    const secondPage = this.props.bookData.slice(15)

    return (
      <div className="mainDiv">
        <div className="bookDivContainer">
            <div className="rightSide">
                {firstPage.map(item => {return ` ${item.sentence}`})}
            </div>
            <div className="leftSide">
              {secondPage.map(item => {return ` ${item.sentence}`})}
            </div>
        </div>
      </div>
    );
  }
}

export default Book;
