import React from "react";
import "./style.css";


function Book(props){
    const firstPage = props.bookData.slice(0,15)
    const secondPage = props.bookData.slice(15)
    return (
      <div className="mainDiv">
        <div className="bookDivContainer">
            <div className="rightSide">
                {firstPage.reverse().map(item => {return ` ${item.sentence}`})}
            </div>
            <div className="leftSide">
                {secondPage.reverse().map(item => {return ` ${item.sentence}`})}
            </div>
        </div>
      </div>
    );
}

export default Book;
