import React, {Component} from "react";
import "./style.css";

class Book extends Component {
  state = {
    pageOneFull: false, 
    pageTwoFull: false, 
    firstPage: [],
    secondPage: [],
  }

  componentDidMount() {
    this.pageFull()
  }

  pageFull = () => {
    const bookData = this.props.bookData
    if (bookData.length > 15) {
      const firstPage = bookData.slice(0,15)
      const secondPage = bookData.slice(15)
      this.setState({
        firstPage: firstPage,
        secondPage: secondPage
      })
    } else {
      this.setState({
        firstPage: bookData,
      })
    }
  }



  render() {

    return (
      <div className="mainDiv">
        <div className="bookDivContainer">
            <div className="rightSide">
                {this.state.firstPage && (this.state.firstPage.map(item => {return ` ${item.sentence}`}))}
            </div>
            <div className="leftSide">
              {this.state.secondPage && (this.state.secondPage.map(item => {return ` ${item.sentence}`}))}
            </div>
        </div>
      </div>
    );
  }
}

export default Book;
